document.addEventListener("DOMContentLoaded",function(){const e=new Splide(".splide__vertical",{direction:"ttb",height:"100vh",arrows:!1,wheel:!0,speed:1e3,classes:{pagination:"splide__pagination splide__pagination--vertical",page:"splide__pagination__page splide__pagination__page--vertical"}});e.mount()});var canvas=document.querySelector(".js-bg-gradient"),ctx=canvas.getContext("2d");function Pixel(e,t){this.x=e,this.y=t,this.hue=Math.floor(360*Math.random());e=.5<Math.random()?-1:1;this.velocity=.01*(30*Math.random()+20)*e}Pixel.prototype.update=function(){this.hue+=this.velocity},Pixel.prototype.render=function(e){var t=Math.round(this.hue);e.fillStyle="hsl("+t+", 100%, 50% )",e.fillRect(this.x,this.y,1,1)};var pixels=[new Pixel(0,0),new Pixel(1,0),new Pixel(0,1),new Pixel(1,1)];function animate(){pixels.forEach(function(e){e.update(),e.render(ctx)}),requestAnimationFrame(animate)}animate();const resources=document.querySelector(".resources"),btnResources=document.querySelector(".resources > h3");btnResources.addEventListener("click",()=>resources.classList.toggle("resources--active")),document.addEventListener("click",function(e){const t=e.target;t.closest(".resources")!==resources&&resources.classList.contains("resources--active")&&resources.classList.remove("resources--active")});